-- Дамп структуры для таблица mc_mvc.news
CREATE TABLE IF NOT EXISTS `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `date` timestamp NOT NULL DEFAULT current_timestamp(),
  `title` varchar(255) NOT NULL,
  `content` text DEFAULT NULL,
  `image` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

-- Дамп данных таблицы mc_mvc.news: ~4 rows (приблизительно)
/*!40000 ALTER TABLE `news` DISABLE KEYS */;
INSERT INTO `news` (`id`, `date`, `title`, `content`, `image`) VALUES
	(1, '2017-07-26 18:48:01', 'Во Всемирный День Доставки «Макдоналдс» приходит домой к москвичам', '<p>Корпорация «Макдоналдс» объявляет 26 июля Всемирным Днем Доставки и в честь этого праздника всем, кто воспользуется услугой по доставке продукции «Макдоналдс» в Москве в этот день, дарит всемирно известный сэндвич Биг Мак. От Нью-Йорка до Новой Зеландии, от Канады до Италии,  – «МакДоставка» доступна в самых разных уголках планеты.</p>\r\n<p>Сегодня  доставку продуктов из меню «Макдоналдс» можно заказать из 7800 ресторанов, расположенных  в 47 странах на 6 континентах. Корпорация «Макдоналдс» с начала нынешнего года утроила количественные показатели по доставке.</p>', 'mc-ubereats.png'),
	(2, '2018-08-23 18:52:18', 'Макдоналдс поможет двум миллионам молодых людей найти работу', '<p>Корпорация Макдоналдс объявила о новой инициативе, «Возможности для молодежи». Ее цель -  к 2025 году создать больше возможностей для трудоустройства двух миллионов молодых людей по всему миру, предоставляя им востребованные на рынке труда навыки, рабочие места и программы обучения и развития. В рамках взятого на себя обязательства Макдоналдс присоединяется к глобальной инициативе «Достойная работа для молодежи» под эгидой Международной организации труда.</p>\r\n<p>Примерно один из четырех молодых людей в мире (21,8%) не работает и не учится. В России, согласно исследованию Высшей школы экономики, более 2 млн молодых людей в возрасте от 15 до 24 лет не заняты ни работой, ни учебой. Таким образом, примерно 15% всей российской молодежи этого возраста исключены из сферы занятости и образования. Это крайне негативно влияет на развитие их профессиональных навыков, долгосрочные карьерные перспективы, и финансовое благополучие. Макдоналдс, будучи одним из крупнейших работодателей для молодежи, обладает потенциалом и масштабами для решения этой проблемы.</p>\r\n<p>В России «Макдоналдс», где работает более 50 000 сотрудников, является одним из крупнейших в отрасли работодателей для молодежи. Для 89% процентов сотрудников от 16 до 21 года «Макдоналдс» — это первая работа. Более 60% сотрудников «Макдоналдс» в России — студенты, которые совмещают работу в ресторанах сети с получением высшего образования. Именно так начинали свою карьеру в компании 98% директоров ресторанов «Макдоналдс». В процессе карьерного роста каждый из них получил более 2000 часов обучения.</p>', 'help-young.jpg'),
	(3, '2018-10-18 19:01:16', '«МакХэппи День» — 2018: старт дан', '<p>Россия, 29 октября 2018 года. Целый месяц – с 20 октября по 19 ноября в каждом из 660 предприятий «Макдоналдс» в 120 городах России проходит семнадцатая ежегодная благотворительная акция «МакХэппи День» в пользу детей, нуждающихся в поддержке. В рамках этого масштабного благотворительного мероприятия часть средств от реализации картофеля фри, а в нынешнем году - и картофеля по-деревенски направляется на проекты и программы Благотворительного Фонда «Дом Роналда Макдоналда». В 2018 году «МакХэппи День» особенный: уже в ноябре в «Макдоналдс» впервые будет продаваться российский картофель фри! В результате многолетней и кропотливой работы теперь этот продукт (один из основных в меню «Макдоналдс») стали выращивать и производить у нас в стране. Символично, что посетители «Макдоналдс» смогут сделать доброе дело, покупая российский товар и поддерживая таким образом не только проекты Фонда, но и отечественного производителя.</p>', 'mc-happy.jpg'),
	(4, '2018-02-06 19:08:53', '«Макдоналдс» продолжит популяризацию здорового образа жизни и семейных ценностей', '<p>«Макдоналдс» продолжит популяризацию здорового образа жизни и семейных ценностей, уделяя особое внимание программе Хэппи Мил на глобальном уровне.</p>\r\n<p>Сегодня корпорация «Макдоналдс» объявила о своем намерении продолжить поддержку семей и здорового образа жизни для всех возрастных категорий, включая детей. Решение принято в рамках глобального плана развития компании и призвано воздействовать на общество во всем мире. «Макдоналдс» принимает на себя обязательства использовать свое влияние, масштаб и охват во всем мире - для того чтобы повлиять на питание детей, оптимизировать доступность чтения и оказать поддержку семьям с детьми с помощью программ Благотворительного фонда «Дом Роналда Макдоналда».</p>\r\n', NULL);
/*!40000 ALTER TABLE `news` ENABLE KEYS */;

