INSERT INTO `products` (`id`, `title`, `price`, `image`, `description`) VALUES
	(1, 'Биг Мак®', 137, 'big-mac.png', 'Большой сандвич с двумя рублеными бифштексами из натуральной цельной говядины на специальной булочке «Биг Мак» ®, заправленной луком, двумя кусочками маринованных огурчиков, ломтиком сыра «Чеддер», свежим салатом, и специальным соусом «Биг Мак»®.'),
	(2, 'Чизбургер Де Люкс', 163, 'cheese-deluxe.png', 'Бифштекс из 100% говядины с кусочком сыра «Чеддер» на карамелизованной булочке с кунжутом, заправленной горчицей, кетчупом, свежим луком, маринованными огурчиками, свежим салатом, ломтиком свежего помидора и специальным соусом «Мак Чикен»®.'),
	(3, 'Картофель фри', 81, 'fries.png', 'Вкусные, обжаренные в растительном фритюре и слегка посоленные палочки картофеля.'),
	(4, 'Картофель по-деревенски', 78, 'potato.png', 'Вкусные, обжаренные в растительном фритюре ломтики картофеля со специями.'),
	(5, 'Чикен Макнаггетс®', 158, 'chicken.png', 'Обжаренные в растительном фритюре кусочки куриного мяса, панированные в сухарях, с горчичным, кисло-сладким, соусом «Барбекю» или «Карри». С каждой порцией два соуса по специальной сниженной цене.'),
	(6, 'Салат овощной', 103, 'salad.png', 'Салат из сочных салатных листьев, помидоров Черри, черных оливок и свежих огурцов, приправленный по Вашему выбору салатными заправками.'),
	(7, 'Вафельный рожок', 30, 'icecream.png', 'Мороженое из натурального цельного молока в вафельном рожке.'),
	(8, 'Кофе Латте', 104, 'latte.png', ''),
	(9, 'Чизкейк', 135, 'cheese-cake.png', 'Песочный бисквит с нежным творожным сыром и добавлением цельного молока.'),
	(10, 'Большие креветки', 173, 'srimp.png', 'Жареные тигровые креветки в хрустящей панировке. Рекомендуем попробовать с соусом 1000 островов. Легко. Изысканно. Вкусно.'),
	(11, 'Пирожок Маракуйя', 50, 'pie.png', ''),
	(12, 'Соус Сальса', 22, 'salsa.png', '');
